insert into products (product_type, name, description, retail_price, discounted_price, image_url) values ('Credit Risk', 'OneView', 'OneView is an industry leading cloud-based solution that provides a comprehensive view of a consumer in a single delivery from multiple sources. The solution delivers our upgraded consumer credit file along with authoritative data only Equifax can deliver using our unique Income and Employment data, our specialty finance DataX report and Utility and Telecom data all delivered via a single inquiry. The OneView solution helps businesses make faster, well informed decisions and also provides a configuration engine that allows our customers to customize the print image version of the OneView report.', 200.00, 0.00, 'https://www.adp.com/-/media/adp/reskin2022/images/whatweoffer/products/run/hero_run.jpg?rev=c0192614b8124fab889a490afbcd7acb&la=en&h=969&w=1320&hash=C599759AB8A942C422F9505EBD63A3A8')
insert into products (product_type, name, description, retail_price, discounted_price, image_url) values ('Verification', 'Prequalification Of One', 'PQO1 allows consumers to check what offers for which they may qualify without a hard inquiry on their credit. From the lender prospective, this is a sales tool that further encourages the consumer to apply for a credit product once they are prequalified.', 250.00, 0.00, 'https://www.adp.com/-/media/adp/reskin2022/images/whatweoffer/products/run/hero_run.jpg?rev=c0192614b8124fab889a490afbcd7acb&la=en&h=969&w=1320&hash=C599759AB8A942C422F9505EBD63A3A8')
insert into products (product_type, name, description, retail_price, discounted_price, image_url) values ('Workforce Management', 'Talent Report™ Education', 'Talent Report™ Education is the first product in the market to deliver a verification of education enabled with SSN search functionality. In an exclusive partnership with the National Student Clearinghouse®, this product provides all available postsecondary degrees and enrollment data per candidate while leveraging candidate-based pricing with a single fulfillment rate. This helps provide greater fee certainty while delivering a better holistic candidate view.', 70.00, 0.00, 'https://www.adp.com/-/media/adp/reskin2022/images/whatweoffer/products/run/hero_run.jpg?rev=c0192614b8124fab889a490afbcd7acb&la=en&h=969&w=1320&hash=C599759AB8A942C422F9505EBD63A3A8')
insert into products (product_type, name, description, retail_price, discounted_price, image_url) values ('Workforce Management', 'Document Verification', 'Document Verification is a multi-level approach to starting consumer relationships off right by verifying customers have known identities and are more likely who they say they are, helping to incrementally increase confidence in identity validity. A consumer’s Personal Identifiable Information (PII) and device presented can be combined with Document Verification’s biometric authentication, using a selfie photo and/or liveness check.', 95.00, 0.00, 'https://www.adp.com/-/media/adp/reskin2022/images/whatweoffer/products/run/hero_run.jpg?rev=c0192614b8124fab889a490afbcd7acb&la=en&h=969&w=1320&hash=C599759AB8A942C422F9505EBD63A3A8' )
insert into products (product_type, name, description, retail_price, discounted_price, image_url) values ('Workforce Management', 'Verification of Education Suite', 'Verifying someone’s education status and degree can be critical for businesses offering products, services, or incentives that are only for students, graduates, or specific degree holders. The Verification of Education (VOEd) product suite provides a reliable means for verifying someone’s enrollment, graduation, and/or degree information at the college level through three types of available verification reports: GradVerify (GV), GradVerify+ (GV+) and Enrollment Verify (EV). Reports are available for the U.S. market.', 120.00, 0.00,  'https://www.adp.com/-/media/adp/reskin2022/images/whatweoffer/products/run/hero_run.jpg?rev=c0192614b8124fab889a490afbcd7acb&la=en&h=969&w=1320&hash=C599759AB8A942C422F9505EBD63A3A8')
insert into products (product_type, name, description, retail_price, discounted_price, image_url) values ('Data-Driven Marketing', 'Wallet Insights', 'Wallet Insights™ provides Equifax data product results in real time, allowing you to apply useful financial and economic insights to individual customers and prospects as they interact with your firm and to improve decision making and customer acquisition efforts.', 230.00, 0.00,  'https://www.adp.com/-/media/adp/reskin2022/images/whatweoffer/products/run/hero_run.jpg?rev=c0192614b8124fab889a490afbcd7acb&la=en&h=969&w=1320&hash=C599759AB8A942C422F9505EBD63A3A8')
insert into products (product_type, name, description, retail_price, discounted_price, image_url) values ('Data-Driven Marketing', 'Property Insights', 'Property Insights enables a comprehensive view of a property with AVM data, lien data, MLS data, market data, and property data. Access AVM data to obtain estimated values of subject properties. Access Lien data to identify mortgage liens on properties including origination date, amount, interest rate, lender name, and more. Access MLS data to identify whether a property is listed for sale.', 150.00, 0.00,  'https://www.adp.com/-/media/adp/reskin2022/images/whatweoffer/products/run/hero_run.jpg?rev=c0192614b8124fab889a490afbcd7acb&la=en&h=969&w=1320&hash=C599759AB8A942C422F9505EBD63A3A8')
insert into products (product_type, name, description, retail_price, discounted_price, image_url) values ('Verification', 'Account Verification', 'Account Verification helps businesses to mitigate risk by verifying both the consumer’s identity and that the active credit card or bank account is associated with that consumer.', 300.00, 0.00,  'https://www.adp.com/-/media/adp/reskin2022/images/whatweoffer/products/run/hero_run.jpg?rev=c0192614b8124fab889a490afbcd7acb&la=en&h=969&w=1320&hash=C599759AB8A942C422F9505EBD63A3A8')
insert into products (product_type, name, description, retail_price, discounted_price, image_url) values ('Verification', 'Asset View', 'Asset View streamlines verifications for both lenders and borrowers making the mortgage process easy and efficient.  Borrowers are required to register and securely link their asset accounts on the consumer portal enabling digital asset verification directly with borrowers’ financial institutions.', 250.00, 0.00,  'https://www.adp.com/-/media/adp/reskin2022/images/whatweoffer/products/run/hero_run.jpg?rev=c0192614b8124fab889a490afbcd7acb&la=en&h=969&w=1320&hash=C599759AB8A942C422F9505EBD63A3A8')
insert into products (product_type, name, description, retail_price, discounted_price, image_url) values ('Credit Risk', 'Persona', 'Persona® provides affordable information that is crucial to the hiring decision. Drawing from the Equifax consumer credit database of over 220 million records, Persona returns a job applicant’s identification information and financial history in a concise, easy-to-read online report. Persona provides valuable insight into an applicant’s financial relationships and performance.', 120.00, 0.00,  'https://www.adp.com/-/media/adp/reskin2022/images/whatweoffer/products/run/hero_run.jpg?rev=c0192614b8124fab889a490afbcd7acb&la=en&h=969&w=1320&hash=C599759AB8A942C422F9505EBD63A3A8')
insert into products (product_type, name, description, retail_price, discounted_price, image_url) values ('Credit Risk', 'Pre-Approval of One', 'Pre-Approval of One allows consumers to initiate an application for credit with no impact to their credit score. Lender evaluates applicant’s creditworthiness and offers a commitment to lend, subject only to collateral requirements or conditions. A hard inquiry will be posted if the consumer moves forward with taking the pre-approved offer and meets the conditional credit requirements.', 80.00, 0.00,  'https://www.adp.com/-/media/adp/reskin2022/images/whatweoffer/products/run/hero_run.jpg?rev=c0192614b8124fab889a490afbcd7acb&la=en&h=969&w=1320&hash=C599759AB8A942C422F9505EBD63A3A8')
insert into products (product_type, name, description, retail_price, discounted_price, image_url) values ('Consumer Engagement', 'Consumer Engagement Suite', 'Provide your customers with single and multi-bureau credit monitoring, credit score and credit report information, directly from your consumer facing platform. All it takes is an API request to deliver the critical credit data your customers need to make more informed decisions about their personal finances.', 320.00, 0.00,  'https://www.adp.com/-/media/adp/reskin2022/images/whatweoffer/products/run/hero_run.jpg?rev=c0192614b8124fab889a490afbcd7acb&la=en&h=969&w=1320&hash=C599759AB8A942C422F9505EBD63A3A8')
